echo $stream->tell();
// 1024

$stream->seek(0);
echo $stream->tell();
// 0

use GuzzleHttp\Psr7;

// Create an empty stream
$stream = Psr7\Utils::streamFor();

// Start dropping data when the stream has more than 10 bytes
$dropping = new Psr7\DroppingStream($stream, 10);

